import React, { Component } from "react";
import { Query, Mutation } from "react-apollo";
import { CURRENT_USER_QUERY } from "./User";
import gql from "graphql-tag";
import { Label, Input, Select, Textarea, Radio, Checkbox } from "@rebass/forms";
import { Box, Flex, Button, Card, Image, Heading, Text } from "rebass";

const UPDATE_PHONE_ADDRESS = gql`
  mutation UPDATE_PHONE_ADDRESS(
    $name: String
    $address: String
    $postal: String
    $phone: String
    $email: String
  ) {
    updatePhoneAddress(
      name: $name
      address: $address
      postal: $postal
      phone: $phone
      email: $email
    ) {
      id
    }
  }
`;

class updatePhoneAddress extends Component {
  state = {
    name: "",
    address: "",
    postal: "",
    email: "",
    phone: ""
  };

  handleChange = e => {
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    this.setState({ [name]: val });
  };

  handleUpdateLocation = () => {
    var event = new Event("input", { bubbles: true });
    this.myinput.dispatchEvent(event);
  };
  render() {
    const maround = [1, 3];
    const pleft = [0, 1, 2, 3];
    const wconst = [1 / 16, 2 / 16];

    // const [county] = this.props;
    return (
      <>
        <Query query={CURRENT_USER_QUERY}>
          {({ data, loading }) => {
            return (
              <Mutation mutation={UPDATE_PHONE_ADDRESS} variables={this.state}>
                {(updatePhoneLocation, { loading, error }) => {
                  return (
                    <div>
                      <Flex flexWrap="wrap">
                        <Box
                          as="form"
                          onSubmit={async e => {
                            e.preventDefault();
                            const res = await updatePhoneLocation();
                          }}
                          pl={pleft}
                          py={3}
                          width={1}
                        >
                          <Flex flexWrap="wrap">
                            <Box width={wconst} mx={maround} pl={pleft}>
                              <Label htmlFor="name">Name</Label>
                              <Input
                                required
                                id="name"
                                name="name"
                                type="text"
                                placeholder="Name"
                                value={this.state.name}
                                onChange={async e => {
                                  this.handleChange(e);
                                }}
                              />
                            </Box>
                            <Box width={wconst} mx={maround}>
                              <Label htmlFor="name">Phone</Label>
                              <Input
                                required
                                id="phone"
                                name="phone"
                                type="text"
                                placeholder="Phone"
                                value={this.state.phone}
                                onChange={async e => {
                                  this.handleChange(e);
                                }}
                              />
                            </Box>
                            <Box width={wconst} mx={maround}>
                              <Label htmlFor="name">Email</Label>
                              <Input
                                required
                                id="email"
                                name="email"
                                type="text"
                                placeholder="Email"
                                value={this.state.email}
                                onChange={async e => {
                                  this.handleChange(e);
                                }}
                              />
                            </Box>
                            <Box width={wconst} mx={maround}>
                              <Label htmlFor="name">Address</Label>
                              <Input
                                required
                                id="address"
                                name="address"
                                type="text"
                                placeholder="Address"
                                value={this.state.address}
                                onChange={async e => {
                                  this.handleChange(e);
                                }}
                              />
                            </Box>
                            <Box width={wconst} mx={maround}>
                              <Label htmlFor="name">Zip Code</Label>
                              <Input
                                required
                                id="zip"
                                name="zip"
                                type="text"
                                placeholder="Zip Code"
                                value={this.state.postal}
                                onChange={async e => {
                                  this.handleChange(e);
                                }}
                              />
                            </Box>
                          </Flex>
                          <Flex mx={-2} flexWrap="wrap">
                            <Box p={2} ml="[3, 4, 5, 6]">
                              <Button type="submit" bg="dark-green">
                                Submit
                              </Button>
                            </Box>
                          </Flex>
                        </Box>
                      </Flex>
                    </div>
                  );
                }}
              </Mutation>
            );
          }}
        </Query>
      </>
    );
  }
}

export default updatePhoneAddress;
