import React from "react";
import AddToCart from "./AddToCart";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import Error from "./ErrorMessage";
import styled from "styled-components";
import Head from "next/head";
import { Box, Flex, Heading, Text, Link, Image } from "rebass";

const SingleItemStyles = styled.div`
  box-shadow: ${props => props.theme.bs};
  min-height: 800px;
  /* img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
  .gallery {
    /* background-color: red; 
    width: 100%;
    height: 100%;
    max-width: 600px;
  } */
`;

const SINGLE_ITEM_QUERY = gql`
  query SINGLE_ITEM_QUERY($id: ID!) {
    item(where: { id: $id }) {
      id
      title
      description
      largeImage
      largeImage1
      largeImage2
    }
  }
`;
const SingleItem = (props, { index }) => {
  console.log(index);
  return (
    <Query query={SINGLE_ITEM_QUERY} variables={{ id: props.id }}>
      {({ error, loading, data }) => {
        if (error) return <Error error={error} />;
        if (loading) return <p>Loading...</p>;
        if (!data.item) return <p>No Item found for {props.id}</p>;
        const item = data.item;
        const imageArray = [];
        const galleryItems = () => {
          imageArray.map(i => <h2 key={i}> {i}</h2>);
        };
        return (
          <SingleItemStyles>
            <Box maxWidth={["90%", "70%"]}>
              <Flex
                flexWrap="wrap"
                justifyContent="space-around"
                backgroundColor="whites.6"
              >
                <Head>
                  <title>AHCA | {item.title}</title>
                </Head>
                <Box width={[1, 1 / 2]}>
                  <Image src={item.largeImage} />
                </Box>
                <Box width={[1, 1 / 2]}>
                  <h2>{item.title}</h2>
                  <p>{item.description}</p>
                </Box>
              </Flex>
            </Box>
          </SingleItemStyles>
        );
      }}
    </Query>
  );
};
export default SingleItem;
export { SINGLE_ITEM_QUERY };
