import React from "react";
import AddToCart from "./AddToCart";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import Error from "./ErrorMessage";
import styled from "styled-components";
import Head from "next/head";
import { Box, Flex, Heading, Text, Link, Image } from "rebass";

const SingleItemStyles = styled.div`
  box-shadow: ${props => props.theme.bs};
  min-height: 800px;
  display: flex;
  /* img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
  .gallery {
    /* background-color: red; 
    width: 100%;
    height: 100%;
    max-width: 600px;
  } */
`;

const SINGLE_ITEM_QUERY = gql`
  query SINGLE_ITEM_QUERY($id: ID!) {
    item(where: { id: $id }) {
      id
      title
      description
      sale
      feature
      highlight
      largeImage
      largeImage1
      largeImage2
    }
  }
`;
const SingleItem = (props, { index }) => {
  return (
    <Query query={SINGLE_ITEM_QUERY} variables={{ id: props.id }}>
      {({ error, loading, data }) => {
        if (error) return <Error error={error} />;
        if (loading) return <p>Loading...</p>;
        if (!data.item) return <p>No Item found for {props.id}</p>;
        const item = data.item;
        console.log(item);
        const imageArray = [];
        const galleryItems = () => {
          imageArray.map(i => <h2 key={i}> {i}</h2>);
        };
        return (
          <SingleItemStyles>
            <Flex flexWrap="wrap" justifyContent="center">
              <Box
                display="flex"
                flexWrap="wrap"
                maxWidth={["90%", "70%"]}
                justifyContent="center"
              >
                <Head>
                  <title>AHCA | {item.title}</title>
                </Head>
                <Box width={[1, 1 / 2]}>
                  <Image src={item.largeImage} />
                </Box>
                <Box width={[1, 1 / 2]}>
                  <Heading>{item.title}</Heading>
                  <h4>{item.description}</h4>
                </Box>
                {/* Second */}
                <Box width={[1, 1 / 2]}>
                  <Image src={item.largeImage1} />
                </Box>
                <Box width={[1, 1 / 2]}>
                  <h3>{item.feature}</h3>
                </Box>
                {/* // Third */}
                <Box width={[1, 1 / 2]}>
                  <Image src={item.largeImage2} />
                </Box>
                <Box width={[1, 1 / 2]}>
                  <h3>{item.highlight}</h3>
                </Box>
              </Box>
            </Flex>
          </SingleItemStyles>
        );
      }}
    </Query>
  );
};
export default SingleItem;
export { SINGLE_ITEM_QUERY };
