import React, { Component } from "react";
import { Query, Mutation } from "react-apollo";
import { CURRENT_USER_QUERY } from "./User";
import gql from "graphql-tag";
import { Label, Input, Select, Textarea, Radio, Checkbox } from "@rebass/forms";
import { Box, Flex, Button, Card, Image, Heading, Text } from "rebass";

const UPDATE_PHONE_ADDRESS = gql`
  mutation UPDATE_PHONE_ADDRESS(
    $name: String
    $address: String
    $postal: String
    $phone: String
    $email: String
  ) {
    updatePhoneAddress(
      name: $name
      address: $address
      postal: $postal
      phone: $phone
      email: $email
    ) {
      id
    }
  }
`;

class updatePhoneAddress extends Component {
  state = {
    name: "",
    address: "",
    postal: "",
    email: "",
    phone: ""
  };

  handleChange = e => {
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    this.setState({ [name]: val });
  };

  handleUpdateLocation = () => {
    var event = new Event("input", { bubbles: true });
    this.myinput.dispatchEvent(event);
  };
  render() {
    const maround = 0;
    const pleft = 0;
    const wconst = [1 / 4, 1];
    const fconst = [1, 2];
    // const hconst = [7, 2];

    // const [county] = this.props;
    return (
      <>
        <Query query={CURRENT_USER_QUERY}>
          {({ data, loading }) => {
            return (
              <Mutation mutation={UPDATE_PHONE_ADDRESS} variables={this.state}>
                {(updatePhoneLocation, { loading, error }) => {
                  return (
                    <div>
                      <Flex flexDirection="column">
                        <Box
                          as="form"
                          onSubmit={async e => {
                            e.preventDefault();
                            const res = await updatePhoneLocation();
                          }}
                          pl={pleft}
                          py={3}
                          fontSize={fconst}
                        >
                          <Flex flexDirection="column" alignItems="center">
                            <Box display="flex" justifyContent="center">
                              <Box width={wconst} mx={maround} pl={pleft}>
                                <Input
                                  required
                                  id="name"
                                  name="name"
                                  type="text"
                                  placeholder="Name"
                                  value={this.state.name}
                                  onChange={async e => {
                                    this.handleChange(e);
                                  }}
                                />
                              </Box>
                              <Box width={wconst} mx={maround}>
                                <Input
                                  required
                                  id="phone"
                                  name="phone"
                                  type="text"
                                  placeholder="Phone"
                                  value={this.state.phone}
                                  onChange={async e => {
                                    this.handleChange(e);
                                  }}
                                />
                              </Box>
                              <Box width={wconst} mx={maround}>
                                <Input
                                  required
                                  id="email"
                                  name="email"
                                  type="text"
                                  placeholder="Email"
                                  value={this.state.email}
                                  onChange={async e => {
                                    this.handleChange(e);
                                  }}
                                />
                              </Box>
                              <Box width={wconst} mx={maround}>
                                <Input
                                  required
                                  id="address"
                                  name="address"
                                  type="text"
                                  placeholder="Address"
                                  value={this.state.address}
                                  onChange={async e => {
                                    this.handleChange(e);
                                  }}
                                />
                              </Box>
                              <Box width={wconst} mx={maround}>
                                <Input
                                  required
                                  id="zip"
                                  name="zip"
                                  type="text"
                                  placeholder="Zip Code"
                                  value={this.state.postal}
                                  onChange={async e => {
                                    this.handleChange(e);
                                  }}
                                />
                              </Box>
                            </Box>
                          </Flex>
                          <Flex mx={-2} flexWrap="wrap">
                            <Box p={2}>
                              <Button type="submit" bg="dark-green">
                                Submit
                              </Button>
                            </Box>
                          </Flex>
                        </Box>
                      </Flex>
                    </div>
                  );
                }}
              </Mutation>
            );
          }}
        </Query>
      </>
    );
  }
}

export default updatePhoneAddress;
