import React from "react";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import Error from "./ErrorMessage";
import styled from "styled-components";
import Head from "next/head";
import AliceCarousel from "react-alice-carousel";
import "./styles/react-alice-carousel.css";

const SingleItemStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${props => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  /* min-height: 800px; */
  .details {
    margin: 3rem;
    font-size: 2rem;
    width: 50%;
  }
  .gallery {
    /* background-color: red; */
    width: 50%;
    height: 50%;
    max-width: 600px;
    /* max-height: 100px; */
  }
`;

const SINGLE_ITEM_QUERY = gql`
  query SINGLE_ITEM_QUERY($id: ID!) {
    item(where: { id: $id }) {
      id
      title
      description
      largeImage
      largeImage1
      largeImage2
    }
  }
`;
export default class SingleItem extends React.Component {
  // componentDidUpdate(prevProps, prevState) {
  //   console.log("componentDidUpdateCalled");
  //   if (this.state.galleryItems !== this.imageArray) {
  //     console.log("componentDidUpdate set new state!");
  //     this.setState({ galleryItems: imageArray });
  //   }
  // }
  responsive = {
    0: { items: 1 }
  };

  onSlideChange(e) {
    console.debug("Item`s position during a change: ", e.item);
    console.debug("Slide`s position during a change: ", e.slide);
  }

  onSlideChanged(e) {
    console.debug("Item`s position after changes: ", e.item);
    console.debug("Slide`s position after changes: ", e.slide);
  }

  render() {
    return (
      <Query query={SINGLE_ITEM_QUERY} variables={{ id: this.props.id }}>
        {({ error, loading, data }) => {
          if (error) return <Error error={error} />;
          if (loading) return <p>Loading...</p>;
          if (!data.item) return <p>No Item found for {this.props.id}</p>;
          const item = data.item;
          const imageArray = [];
          return (
            <SingleItemStyles>
              <Head>
                <title>AHCA | {item.title}</title>
              </Head>
              <div className="gallery">
                <AliceCarousel
                  responsive={this.responsive}
                  autoPlayInterval={5000}
                  autoPlayDirection="rtl"
                  autoPlay={true}
                  fadeOutAnimation={true}
                  mouseTrackingEnabled={true}
                  mouseDragEnabled={true}
                  playButtonEnabled={false}
                  disableAutoPlayOnAction={true}
                  onSlideChange={this.onSlideChange}
                  onSlideChanged={this.onSlideChanged}
                >
                  <img src={item.largeImage} />
                  {item.largeImage1 !== null && <img src={item.largeImage1} />}
                  {console.log(item.largeImage2)}
                  {item.largeImage2 !== null && <img src={item.largeImage2} />}
                </AliceCarousel>
              </div>
              <div className="details">
                <h2>{item.title}</h2>
                <p>{item.description}</p>
              </div>
            </SingleItemStyles>
          );
        }}
      </Query>
    );
  }
}

export { SINGLE_ITEM_QUERY };
