import React, { Component } from "react";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import { Label, Input, Select, Textarea, Radio, Checkbox } from "@rebass/forms";
import { Box, Flex, Button, Card, Image, Heading, Text } from "rebass";

const UPDATE_PHONE_LOCATION = gql`
  mutation UPDATE_PHONE_LOCATION(
    $name: String
    $location: String
    $phone: String
    $email: String
  ) {
    updatePhoneLocation(
      name: $name
      location: $location
      phone: $phone
      email: $email
    ) {
      id
    }
  }
`;

export default class LocationCard extends Component {
  state = {
    name: "",
    location: "",
    phone: "",
    email: ""
  };

  // componentDidUpdate(prevProps, prevState) {
  //   console.log("prevState", prevState);
  //   console.log("state", this.state);
  //   console.log("states match?", prevState === this.state ? "true" : "false");
  //   if (this.state !== prevState) {
  //     this.setState(prevState => ({
  //       ...prevState,
  //       name: this.state.name,
  //       location: this.state.location,
  //       phone: this.state.phone,
  //       email: this.state.email
  //     }));
  //   }
  // }

  handleChange = e => {
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    this.setState({ [name]: val });
  };

  render() {
    const maround = [1, 3];
    const pleft = [0, 1, 2, 3];
    const wconst = [1, 1 / 4];
    return (
      <Mutation mutation={UPDATE_PHONE_LOCATION} variables={this.state}>
        {(updatePhoneLocation, { loading, error }) => (
          <div>
            <Box
              as="form"
              onSubmit={e => e.preventDefault()}
              pl={pleft}
              py={3}
              width={1}
            >
              <Heading pcolor="near-black" fontSize={[4, 5, 6]}>
                Are we in your neighboorhood?
              </Heading>
              <Flex flexWrap="wrap">
                <Box width={wconst} mx={maround} pl={pleft}>
                  <Label htmlFor="name">Name</Label>
                  <Input
                    id="name"
                    name="name"
                    type="text"
                    placeholder="Name"
                    value={this.state.name}
                    onChange={this.handleChange}
                  />
                </Box>
                <Box width={wconst} mx={maround}>
                  <Label htmlFor="name">Phone</Label>
                  <Input id="phone" name="phone" defaultValue="" />
                </Box>
                <Box width={wconst} mx={maround}>
                  <Label htmlFor="location">Location</Label>
                  <Heading
                    fontSize={[3, 4, 5]}
                    color="near-black"
                    value={this.props.county}
                    onChange={this.handleChange}
                  >
                    {this.props.county}
                  </Heading>
                </Box>
              </Flex>
              <Flex mx={-2} flexWrap="wrap">
                <Box p={2} ml="[3, 4, 5, 6]">
                  <Button type="submit" bg="dark-green">
                    Submit
                  </Button>
                </Box>
              </Flex>
            </Box>
          </div>
        )}
      </Mutation>
    );
  }
}
