import React from "react";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import { CURRENT_USER_QUERY } from "./User";
import User from "./User";

const ADD_TO_CART_MUTATION = gql`
  mutation addToCart($id: ID!) {
    addToCart(id: $id) {
      id
      quantity
    }
  }
`;

const CREATE_GUEST_MUTATION = gql`
  mutation CREATE_GUEST_MUTATION(
    $email: String
    $name: String
    $password: String
  ) {
    createGuest(email: $email, name: $name, password: $password) {
      id
      email
      name
    }
  }
`;

const AddToCart = ({ id }) => (
  <User>
    {({ data }) => {
      let me = data ? data.me : null;
      let guest = me === null ? true : false;
      console.log(guest);
      return (
        <Mutation
          mutation={(ADD_TO_CART_MUTATION, CREATE_GUEST_MUTATION)}
          variables={{ id }}
          refetchQueries={[{ query: CURRENT_USER_QUERY }]}
        >
          {(addToCart, createGuest) => {
            return <button onClick={addToCart}> Add to Cart ðŸ›’ </button>;
          }}
        </Mutation>
      );
    }}
  </User>
);
export default AddToCart;
export { ADD_TO_CART_MUTATION };
