import React, { useState } from "react";
import { Query, Mutation } from "react-apollo";
import { CURRENT_USER_QUERY } from "./User";
import gql from "graphql-tag";
import { Label, Input, Select, Textarea, Radio, Checkbox } from "@rebass/forms";
import { Flex, Heading, Box, Button } from "rebass";

const VALIDATE_APPLY_PROMO_CODE = gql`
  mutation updatePriceAfterPromoCode($id: ID!, $promotion: String!) {
    updatePriceAfterPromoCode(id: $id, promotion: $promotion) {
      id
      promotionPrice
    }
  }
`;

const PromoCode = props => {
  const [promoState, setPromoState] = useState("");
  const maround = [1, 3];
  const pleft = [0, 1, 2, 3];
  const wconst = [1, 1 / 4];
  console.log(props);
  return (
    <>
      <Query query={CURRENT_USER_QUERY}>
        {({ data, loading }) => {
          return (
            <Mutation
              mutation={VALIDATE_APPLY_PROMO_CODE}
              variables={{ id: props.id }}
            >
              {(updatePriceAfterPromoCode, { loading, error }) => {
                return (
                  <Box
                    as="form"
                    onSubmit={async e => {
                      e.preventDefault();
                      const res = await updatePriceAfterPromoCode();
                    }}
                    pl={pleft}
                    py={3}
                    width={1}
                  >
                    <Heading pcolor="near-black" fontSize={[4, 5, 6]}>
                      Do you have a promo code?
                    </Heading>
                    <Flex flexWrap="wrap">
                      <Box width={wconst} mx={maround} pl={pleft}>
                        <Label htmlFor="promo">Promo Code</Label>
                        <Input
                          id="promo"
                          name="promo"
                          type="text"
                          placeholder="Promo Code"
                          value={promoState}
                          onChange={async e => {
                            setPromoState(e.target.value);
                          }}
                        />
                      </Box>
                    </Flex>
                    <Flex mx={-2} flexWrap="wrap">
                      <Box p={2} ml="[3, 4, 5, 6]">
                        <Button type="submit" bg="dark-green">
                          Submit
                        </Button>
                      </Box>
                    </Flex>
                  </Box>
                );
              }}
            </Mutation>
          );
        }}
      </Query>
    </>
  );
};

export default PromoCode;
