import React from "react";
import AddToCart from "./AddToCart";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import Error from "./ErrorMessage";
import styled from "styled-components";
import Head from "next/head";

const SingleItemStyles = styled.div`
  max-width: 1200px;
  margin: 2rem auto;
  box-shadow: ${props => props.theme.bs};
  display: grid;
  grid-auto-columns: 1fr;
  grid-auto-flow: column;
  min-height: 800px;
  /* img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
  .details {
    margin: 3rem;
    font-size: 2rem;
  }
  .gallery {
    /* background-color: red; 
    width: 100%;
    height: 100%;
    max-width: 600px;
  } */
`;

const SINGLE_ITEM_QUERY = gql`
  query SINGLE_ITEM_QUERY($id: ID!) {
    item(where: { id: $id }) {
      id
      title
      description
      largeImage
      largeImage1
      largeImage2
    }
  }
`;
const SingleItem = ({ index }) => {
  console.log(index);
  return (
    <Query query={SINGLE_ITEM_QUERY} variables={{ id: props.id }}>
      {({ error, loading, data }) => {
        if (error) return <Error error={error} />;
        if (loading) return <p>Loading...</p>;
        if (!data.item) return <p>No Item found for {props.id}</p>;
        const item = data.item;
        const imageArray = [];
        const galleryItems = () => {
          imageArray.map(i => <h2 key={i}> {i}</h2>);
        };
        return (
          <SingleItemStyles>
            <Head>
              <title>AHCA | {item.title}</title>
            </Head>
            <div className="gallery">
              <img src={item.largeImage} />
              <img src={item.largeImage1} />
              <img src={item.largeImage2} />
            </div>
            <div className="details">
              <h2>{item.title}</h2>
              <p>{item.description}</p>
            </div>
          </SingleItemStyles>
        );
      }}
    </Query>
  );
};
export default SingleItem;
export { SINGLE_ITEM_QUERY };
