import React, { Component } from "react";
import styled from "styled-components";
import Raise from "./Raise";
import { Box, Flex, Button, Card, Image, Heading, Text } from "rebass";
import Link from "next/link";

const pleft = [3, 4, 5];

const Divider = props => (
  <Box
    {...props}
    as="hr"
    sx={{
      bg: "dark-gray",
      border: 0,
      height: 2
    }}
  />
);

const ItemsFrontPage = props => {
  const { item } = props;
  const { index } = props;
  return index % 2 === 0 ? (
    <Flex flexDirection="row" justifyContent="space-around">
      <Box width={[2 / 5, 1 / 2]} py={3} px={3}>
        <Image
          src={item.largeImage}
          sx={{
            height: ["100%", "50%"]
          }}
        />
      </Box>
      <Box width={[3 / 5, 1 / 2]} py={3} px={3}>
        <Heading color="near-black" fontSize={[5, 4, 5, 6]}>
          {item.title}
        </Heading>
        <Divider />
        <Heading color="dark-gray" fontSize={[4, 3, 4, 5]} pl={pleft} pt={4}>
          {item.description}
        </Heading>
      </Box>
    </Flex>
  ) : (
    <Flex flexDirection="row" justifyContent="space-around">
      <Box width={[3 / 5, 1 / 2]} py={3} px={3}>
        <Heading color="near-black" fontSize={[5, 4, 5, 6]} pl={pleft}>
          {item.title}
        </Heading>
      </Box>
      <Box width={[2 / 5, 1 / 2]} py={3} px={3}>
        <Image src={item.largeImage} />
      </Box>
    </Flex>
  );
};

export default ItemsFrontPage;
