import React, { useState } from "react";
import { Query, Mutation } from "react-apollo";
import User from "./User";
import gql from "graphql-tag";
import CartStyles from "./styles/CartStyles";
import Supreme from "./styles/Supreme";
import CloseButton from "./styles/CloseButton";
import SickButton from "./styles/SickButton";
import CartItem from "./CartItem";
import calcTotalPrice from "../lib/calcTotalPrice";
import formatMoney from "../lib/formatMoney";
import { adopt } from "react-adopt";
import TakeMyMoney from "./TakeMyMoney";
import SignUpButton from "./SignUpButton";

const UPDATE_PHONE_ADDRESS = gql`
  mutation UPDATE_PHONE_ADDRESS(
    $name: String
    $address: String
    $postal: String
    $phone: String
    $email: String
  ) {
    updatePhoneLocation(
      name: $name
      address: $address
      postal: $postal
      phone: $phone
      email: $email
    ) {
      id
    }
  }
`;

const LOCAL_STATE_QUERY = gql`
  query {
    cartOpen @client
  }
`;

const TOGGLE_CART_MUTATION = gql`
  mutation {
    toggleCart @client
  }
`;

const Composed = adopt({
  user: ({ render }) => <User>{render}</User>,
  // cartItem: ({ render }) => <CartItem>{render}</CartItem>,
  toggleCart: ({ render }) => (
    <Mutation mutation={TOGGLE_CART_MUTATION}>{render}</Mutation>
  ),
  localState: ({ render }) => <Query query={LOCAL_STATE_QUERY}>{render}</Query>
});

const Cart = (props, { item }) => {
  const [nameState, setNameState] = useState("");
  const [addressState, setAddressState] = useState("");
  const [postalState, setPostalState] = useState("");
  const [phoneState, setPhoneState] = useState("");
  const [emailState, setEmailState] = useState("");
  return (
    <Composed>
      {({ user, toggleCart, localState }) => {
        <Mutation
          mutation={UPDATE_PHONE_ADDRESS}
          variables={
            (nameState, addressState, postalState, phoneState, emailState)
          }
        >
          {(createGuest, { loading, error }) => {
            console.log("user.data", user.data);
            {
              /* console.log("cartItemwithinCart", cartItem); */
            }
            const me = user.data ? user.data.me : null;
            if (!me) return null;
            setNameState(me?.name);
            setAddressState(me?.address);
            setPostalState(me?.postal);
            setPhoneState(me?.phone);
            setEmailState(me?.email);
            return (
              <CartStyles open={localState.data.cartOpen}>
                <header>
                  <CloseButton onClick={toggleCart} title="close">
                    &times;
                  </CloseButton>
                  {me.permissions.includes("GUEST") ? (
                    <Supreme>Guest Cart</Supreme>
                  ) : (
                    <Supreme>{me.name}'s Cart</Supreme>
                  )}
                  <p>
                    You have {me.cart.length} item
                    {me.cart.length === 1 ? "" : "s"} in your cart
                  </p>
                </header>
                <ul>
                  {me.cart.map(cartItem => (
                    <>
                      <CartItem key={cartItem.id} cartItem={cartItem} />
                    </>
                  ))}
                </ul>
                <footer>
                  <p>{formatMoney(calcTotalPrice(me.cart))}</p>

                  {me.cart.length ? (
                    <TakeMyMoney>
                      <SickButton disabled={false}>Checkout</SickButton>
                    </TakeMyMoney>
                  ) : me.permissions.includes("GUEST") ? (
                    <SickButton>
                      <SignUpButton>Register</SignUpButton>
                    </SickButton>
                  ) : (
                    <TakeMyMoney>
                      <SickButton disabled={true}>Checkout</SickButton>
                    </TakeMyMoney>
                  )}
                </footer>
              </CartStyles>
            );
          }}
        </Mutation>;
      }}
    </Composed>
  );
};

export default Cart;
export { LOCAL_STATE_QUERY, TOGGLE_CART_MUTATION };
