import React from "react";
import User from "./User";
import DeleteItem from "./DeleteItem";
import Link from "next/link";
import Title from "./styles/Title";
import ItemStyles from "./styles/ItemStyles";
import PriceTag from "./styles/PriceTag";
import formatMoney from "../lib/formatMoney";
import AddToCart from "./AddToCart";
import PropTypes from "prop-types";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import { CURRENT_USER_QUERY } from "./User";

const CREATE_GUEST_MUTATION = gql`
  mutation CREATE_GUEST_MUTATION(
    $email: String
    $name: String
    $password: String
  ) {
    createGuest(email: $email, name: $name, password: $password) {
      id
      email
      name
    }
  }
`;

const Item2 = ({ item, id }) => (
  <User>
    {({ data }) => {
      let me = data ? data.me : null;
      let guest = me === null ? true : false;
      return (
        <ItemStyles>
          {item.image && (
            <Link
              href={{
                pathname: "/item",
                query: { id: item.id }
              }}
            >
              <img src={item.image} alt={item.title} />
            </Link>
          )}
          <Title>
            <Link
              href={{
                pathname: "/item",
                query: { id: item.id }
              }}
            >
              <a>{item.title}</a>
            </Link>
          </Title>
          <PriceTag>{formatMoney(item.price)}</PriceTag>
          <div className="buttonList">
            {!guest && me.permissions.includes("ADMIN") && (
              <Link
                href={{
                  pathname: "update",
                  query: { id: item.id }
                }}
              >
                <a>Edit ✏️</a>
              </Link>
            )}
            {guest && (
              <Mutation
                mutation={CREATE_GUEST_MUTATION}
                variables={{ id }}
                refetchQueries={[{ query: CURRENT_USER_QUERY }]}
              >
                {(createGuest, { loading, error }) => {
                  return <AddToCart id={item.id} onclick={createGuest} />;
                }}
              </Mutation>
            )}
            {!guest && <AddToCart id={item.id} />}

            {!guest && me.permissions.includes("ADMIN") && (
              <DeleteItem id={item.id}>Delete This Item</DeleteItem>
            )}
          </div>
        </ItemStyles>
      );
    }}
  </User>
);

Item2.propTypes = {
  item: PropTypes.object.isRequired
};

export default Item2;
