import React, { Component } from "react";
import styled from "styled-components";
import Raise from "./Raise";
import { Box, Flex, Button, Card, Image, Heading, Text } from "rebass";
import Link from "next/link";

const pleft = [3, 4, 5];

const Divider = props => (
  <Box
    {...props}
    as="hr"
    sx={{
      bg: "dark-gray",
      border: 0,
      height: 2
    }}
  />
);

const ItemsFrontPage = props => {
  const {
    item: {
      categories,
      feature,
      highlight,
      sale,
      price,
      description,
      image,
      largeImage
    }
  } = props;
  const { index } = props;
  return index % 2 === 0 ? (
    <Flex alignItems="center" justifyContent="center" flexWrap="wrap">
      <Box width={[1, 1 / 2]} py={3}>
        <Heading color="near-black" fontSize={[4, 6]} pl={pleft}>
          {categories}
        </Heading>
        <Raise distance="50%">
          <Heading pl={pleft} fontSize={[5, 7]} color="near-black">
            {title}
          </Heading>
        </Raise>

        <Text fontSize={[1, 2]} pl={pleft} fontWeight="bold">
          {description}
        </Text>
        <Text fontSize={[1, 2]} pl={pleft}>
          <ul>
            <li>{feature}</li>
            <li>{highlight}</li>
          </ul>
        </Text>
        <Button ml={pleft} bg="dark-green" color="white">
          <Link href="/shop">
            <a>
              <Text fontWeight="bold" color="white">
                Shop Now
              </Text>
            </a>
          </Link>
        </Button>
      </Box>
      <Box
        width={[1, 1 / 2]}
        my={[4, 5]}
        alignItems="center"
        justifyContent="center"
        py={3}
      >
        <Image src={image} />
      </Box>
    </Flex>
  ) : (
    <Flex alignItems="center" justifyContent="center" flexWrap="wrap">
      <Box
        width={[1, 1 / 2]}
        my={[4, 5]}
        alignItems="center"
        justifyContent="center"
        py={3}
      >
        <Image src={image} />
      </Box>
      <Box width={[1, 1 / 2]} py={3}>
        <Heading color="near-black" fontSize={[4, 6]} pl={pleft}>
          {categories}
        </Heading>
        <Raise distance="50%">
          <Heading pl={pleft} fontSize={[5, 7]} color="near-black">
            {title}
          </Heading>
        </Raise>

        <Text fontSize={[1, 2]} pl={pleft} fontWeight="bold">
          {description}
        </Text>
        <Text fontSize={[1, 2]} pl={pleft}>
          <ul>
            <li>{feature}</li>
            <li>{highlight}</li>
          </ul>
        </Text>
        <Button ml={pleft} bg="dark-green" color="white">
          <Link href="/shop">
            <a>
              <Text fontWeight="bold" color="white">
                Shop Now
              </Text>
            </a>
          </Link>
        </Button>
      </Box>
    </Flex>
  );
};

export default ItemsFrontPage;
