export default function calcTotalPrice(cart) {
  console.log(cart);
  var promoItems = cart.filter(function (cartItem) {
    return cartItem.promotionPrice !== null;
  });
  var regularItems = cart.filter(function (cartItem) {
    return cartItem.promotionPrice === null;
  });
  var promoAmount = promoItems.reduce(function (tally, cartItem) {
    return tally + cartItem.promotionPrice * cartItem.quantity;
  }, 0);
  var regularAmount = regularItems.reduce(function (tally, cartItem) {
    return tally + cartItem.item.price * cartItem.quantity;
  }, 0);
  console.log(promoAmount);
  console.log(regularAmount);
  return cart.reduce(function (tally, cartItem) {
    if (!cartItem.item) return tally;
    return regularAmount + promoAmount;
  }, 0);
}
